//Adds the cell to the cell collection.
selectedCells.Add(cell);

//Gets a cell using cell name.
cell = worksheet.Cells["F10"];
//Adds the cell to the cell collection.
selectedCells.Add(cell);

//Clears the cell collection.
selectedCells.Clear();

