ICommentCollection comments = worksheet.Comments;

//Removes the comment of the specific cell.
comments.RemoveAt(1, 1);

//Saves as the specified file format.
workbook.SaveAs(@"D:\Book1.xls", FileFormat.Excel97To2003);


