 workbook.Worksheets[0].ImportArray(
     new object[,] {              
                           {"xh","name","age" },
                           {1,"james",56 },
                           {2,"Chan K.S",34}
                        },   0, 0);
  workbook.SaveToHttpResponse(this.Response, "book1.xls", FileFormat.Excel97To2003, false);

